import React from "react";
import { useState } from "react";
import { TodoContext } from "../TodoContext";

const EditForm = () => {
  const { setEditOpenModal, oldTodo, setOldTodo, totalTodos } =
    React.useContext(TodoContext);

  const [newTodo, setNewTodo] = React.useState({});

  const onCancel = () => {
    setEditOpenModal(false);
  };

  const onChange = (event) => {
    setNewTodo({ text: event.target.value, completed: false, id: oldTodo.id });
  };

  const onSubmit = (e) => {
    e.preventDefault();
    console.log(newTodo);
    const updatedOldTodo = setOldTodo(newTodo);
    console.log(updatedOldTodo);
    setEditOpenModal(false);
  };

  return (
    <div className="ModalBackground">
      <form onSubmit={onSubmit}>
        <label>Editando huesos</label>
        <textarea onChange={onChange} placeholder={oldTodo.text} />

        <div className="TodoForm-buttonContainer">
          <button type="button" onClick={onCancel} className="TodoForm-button">
            cancelar
          </button>
          <button type="submit" className="TodoForm-button TodoForm-button-add">
            Actualizar hueso
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditForm;
